import React, { Component } from 'react'
import "./AddPersonStyles.css";

class Interests extends Component {

    
    constructor(props) {
        super(props);
        this.state = {
           
          
            
        }
    }
        
       
    

    

   

    render() {
        const { displayChildren} = this.state; 
        


        let children; 

        if(displayChildren){
            children = (
                <div>
                    <Interests/>
                </div>
            )
        }

        return (
            <div>
                <div className = "d-flex flex-row ml-2 bd-highlight mb-1">
                                    
                    <button type = "button" className = "btn btn-info ml-2" onClick ={() => {
                            this.setState(prevState => ({
                                displayChildren: !prevState.displayChildren
                            }))
                        }}>
                        Interests (Click to expand)
                    </button> 
                </div>
                <div className = "col">
                    {children}
                </div>

                
            </div>
        )
    }
}



export default Interests;
