import React, { Component } from 'react'
import "./AddPersonStyles.css";
import TextFieldGroup from "../common/TextFieldGroup";
import {addNode,getNode} from "../../actions/nodeActions";
import {connect} from 'react-redux';
import {withRouter} from "react-router-dom";
import PropTypes from 'prop-types';


class Interests extends Component {

    
    constructor(props) {
        super(props);
        this.state = {
           
           name: ""
        }
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
        
    componentDidMount(){
        this.props.getNode();
        console.log(node);
    }
       
    onSubmit(e){
        e.preventDefault();

        const data = {
            name: this.state.name   
        }
        
            
        console.log(data);
        this.props.addNode(data);
    }

    onChange(e) {
        
            this.setState({[e.target.name]: e.target.value});
        
    }

    

   

    render() {
        const { displayChildren} = this.state; 
        const {node} = this.props.node; 


        let children; 

        if(displayChildren){
            children = (
                <div>
                    <Interests/>
                </div>
            )
        }else{
            children = (
                
                    <form  onSubmit = {this.onSubmit}>
                        <div className = "row derp">
                            <input
                    
                                className = "form-control squish form-control-lg" 
                                placeholder="name"
                                name="name"
                                value = {this.state.name}
                                onChange = {this.onChange}
                                autoComplete = "off"
                            />             
                                      
                                
                               
                            <input type = "submit" value= "+" className = "btn btn-info butt"/>
                        </div>
                    </form>      
                    
                 
            )
        }

        return (
            <div>
                <div className = "d-flex flex-row ml-2 bd-highlight mb-2">
                                    
                    <button type = "button" className = "btn btn-info ml-2" onClick ={() => {
                            this.setState(prevState => ({
                                displayChildren: !prevState.displayChildren
                            }))
                        }}>
                        Interests (Click to expand)
                    </button> 
                </div>
                <div className = "col">
                    {children}
                    
                </div>

                
            </div>
        )
    }
}

Interests.propTypes = {
    getNode: PropTypes.func.isRequired,
    node: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    node: state.node
})


export default connect(mapStateToProps, {addNode, getNode})(withRouter(Interests));
