import React, { Component } from 'react'
import TextFieldGroup from "../common/TextFieldGroup";

class Login extends Component {

    constructor(props) {
        super(props);
        this.state = {
           
            email: "",
            password: "",
            password2: ""
        }
        
        //this.props.addNode("Books", "Conceptual");
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        
    }

    onSubmit(e){
        e.preventDefault();

        
        //console.log(this.props.interests, "interests here");
        const profileData = {
            email: this.state.email,
            password: this.state.password,
            password2: this.state.password2
        }
        
        //this.props.createProfile(profileData, this.props.history);
    }

    onChange(e) {
        
        this.setState({[e.target.name]: e.target.value});
    
    }

  render() {
      const {displaySignUp} = this.state;
      let display; 

      if(displaySignUp){
          display = (
            <div className = " row align-self-center mt-5 ">

                <div className = "col-md-8  m-auto">
                    <form  onSubmit = {this.onSubmit}>
                    
                        <TextFieldGroup 
                            placeholder = "email"
                            name = "email"
                            value = {this.state.email}
                            onChange = {this.onChange}
                            
                            info = "email"
                            autoComplete = "off"
                            
                        />
                        
                        <input 

                            autoComplete = "new-password"
                            type ="password"
                            className="form-control form-control-lg" 
                            placeholder="password"
                            name="password"
                            value = {this.state.password} 
                            onChange = {this.onChange}
                        
                            
                        />  

                        <input 

                            autoComplete = "new-password"
                            type ="password"
                            className="form-control form-control-lg" 
                            placeholder="confirm password"
                            name="password2"
                            value = {this.state.password2} 
                            onChange = {this.onChange}


                        />  
                        
                        
                        
                        

                    
                        <input type = "submit" value= "Create account" className = "btn btn-info btn-block mt-4"/>
                    </form> 
                    
                </div>
            </div>  
          )
      }else{
          display = (
            <div className = " row align-self-center mt-5 ">

                <div className = "col-md-8  m-auto">
                    <form  onSubmit = {this.onSubmit}>
                    
                        <TextFieldGroup 
                            placeholder = "email"
                            name = "email"
                            value = {this.state.email}
                            onChange = {this.onChange}
                            
                            info = "email"
                            autoComplete = "off"
                            
                        />
                        
                        <input 

                            autoComplete = "new-password"
                            type ="password"
                            className="form-control form-control-lg" 
                            placeholder="password"
                            name="password"
                            value = {this.state.password} 
                            onChange = {this.onChange}
                        
                            
                        />  
                        
                        
                        
                        

                    
                        <input type = "submit" value= "Login" className = "btn btn-info btn-block mt-4"/>
                    </form> 
                    <div className = "btn btn-info btn-block mt-4" onClick ={() => {
                            this.setState(prevState => ({
                                displaySignUp: !prevState.displaySignUp
                            }))}}>>Sign Up
                    </div>  
                </div>
            </div>
          )
      }
    return (
        <div className = "hold bg-dark">
            <div className = "row">
                <div className = "col-4">
                    {/* <div className = "addPerson" >
                        <Dashboard />
                    </div> */}
                
                </div> 

                <div className = "col-4 ">
                    
                    <div className = "profileContainer container">
                        <div className = "row ">
                            <div className = "col-sm"></div>
                            <div className = "col-sm">
                                <h1>Nector</h1>
                            </div>
                            <div className = "col-sm"></div>
                        </div>
                        

                            
                        {display}

                        

                    </div>

                </div>

                
            </div>
        </div>
    )
  }
}

export default Login;
