import React, { Component } from 'react'

 class Welcome extends Component {

  constructor(props) {
    super(props);
    this.state = {
       
        name: "",
        
    }
    
    //this.props.addNode("Books", "Conceptual");
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    
  }

  onSubmit(e){
    e.preventDefault();

    
    //console.log(this.props.interests, "interests here");
    const profileData = {
        name: this.state.name,
        
    }
  
  }

  onChange(e) {
        
    this.setState({[e.target.name]: e.target.value});

  }

  render() {
    return (
      <div className = "container ">
        <div className = "row">
          <h1 className = "welcome m-auto">Welcome</h1>  
          <p className = "ml-2 mr-2">My name is Nector, and I am here to help you connect!</p>
          <p className = "ml-2 mr-2">What is your name?</p>

          <form  onSubmit = {this.onSubmit}>
            <input 

              autoComplete = "new-password"
              type ="text"
              className="form-control form-control-lg m-auto" 
              placeholder="name"
              name="name"
              value = {this.state.name} 
              onChange = {this.onChange}


            />          
            <input type = "submit" value= "Submit" className = "btn btn-info btn-block mt-4"/>
          </form>                     
                                
                                
                                
                                
                                
                               
                               
          <p className = "ml-2 mr-2">Nector allows users to create placeholder profiles for their friends, to help
            them organize their friends,
            even before their friend has created an account. But once you do create an account, 
            you can point these placeholders to yourself. Search your name below to see if 
            someone has created a placeholder profile.
          </p>
        </div>
        
          
      </div>
      
    )
  }
}

export default Welcome;
