import React, { Component } from 'react'
import {connect} from 'react-redux';
import {getPosts, addPosts} from "../../actions/postActions";
import TextAreaFieldGroup from "../common/TextAreaFieldGroup";
import {withRouter} from "react-router-dom";
import PropTypes from 'prop-types';

class Chat extends Component {

    constructor(props) {
        super(props);
        this.state = {
            post: "",
            
            
        }
        
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.props.getPosts(); 
    }

    onSubmit(e){
        e.preventDefault();

        let data = {
            post: this.state.post
        }
        this.props.addPosts(data); 
        this.props.getPosts(); 
        
        
    }

    onChange(e) {
        
        this.setState({[e.target.name]: e.target.value});
    
    }


    render() {
        let listPosts; 

        if(this.props.posts.posts.posts){
            listPosts = (
                <p className = "text-light">{this.props.posts.posts.posts[0].body}</p>
            )
        }

        return (
        <div>
            
            <form  onSubmit = {this.onSubmit}>
                                    
                <TextAreaFieldGroup
                    placeholder = "post"
                    name = "post"
                    value = {this.state.post}
                    onChange = {this.onChange}
                    
                    info = "post"
                    autoComplete = "off"
                    
                />

                <input type = "submit" value= "Submit" className = "btn btn-info btn-block mt-2"/>
            </form>   

            <div className = "container mt-3">
                <div className = "col">
                    {listPosts}
                    <p className = "text-light"></p>
                </div>

                   
            </div>
                             
        </div>
        )
    }
}


Chat.propTypes = {
    posts: PropTypes.object.isRequired,
    getPosts: PropTypes.func.isRequired,
    addPosts: PropTypes.func
}

const mapStateToProps = state => ({
    posts: state.posts
    
});

export default connect(mapStateToProps, {getPosts, addPosts})(withRouter(Chat))
