import React, { Component } from 'react'
import "./AddPersonStyles.css";
import TextFieldGroup from "../common/TextFieldGroup";
import {addNode} from "../../actions/nodeActions";
import {connect} from 'react-redux';
import {withRouter} from "react-router-dom";
import PropTypes from 'prop-types';

class Interests extends Component {

    
    constructor(props) {
        super(props);
        this.state = {
           
           name: ""
            
        }
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
        
       
    onSubmit(e){
        e.preventDefault();

        const data = {
            name: this.state.name   
        }
        
            
        //console.log(profileData);
        this.props.addNode(data);
    }

    onChange(e) {
        
            this.setState({[e.target.name]: e.target.value});
        
    }

    

   

    render() {
        const { displayChildren} = this.state; 
        


        let children; 

        if(displayChildren){
            children = (
                <div>
                    <Interests/>
                </div>
            )
        }

        return (
            <div>
                <div className = "d-flex flex-row ml-2 bd-highlight mb-2">
                                    
                    <button type = "button" className = "btn btn-info ml-2" onClick ={() => {
                            this.setState(prevState => ({
                                displayChildren: !prevState.displayChildren
                            }))
                        }}>
                        Interests (Click to expand)
                    </button> 
                </div>
                <div className = "col">
                    {children}
                    <form  onSubmit = {this.onSubmit}>
                               
                        <TextFieldGroup 
                              placeholder = "name"
                              name = "name"
                              value = {this.state.name}
                              onChange = {this.onChange}
                              
                              info = "name"
                              autoComplete = "off"
                              
                        />              
                                
                               
                        <input type = "submit" value= "Submit" className = "btn btn-info btn-block mt-4"/>
                    </form>   
                </div>

                
            </div>
        )
    }
}




export default connect({addNode})(withRouter(Interests));
